from bioplot import Bioplot


def test_plot():
    import matplotlib.pyplot as plt
    import numpy as np
    fig = plt.figure()
    ax = fig.add_subplot(111)
    bplot = Bioplot(ax)

    seq = ""
    colors = np.random.randn(len(seq))

    bplot.aaplot2d(seq, colors=colors, max_width=20)
    plt.savefig("example.png")
