class fundamental:
	avo = 6.02214*(10**23) #avogadro-const
	far = 96485.33 #faraday-const
	amc = 1.660538*(10**-27) #atomic-mass-const
	mgc = 8.314462618 #molar-gas-const
	gas = 8.250*1000 #gas-const
	col = 8.987551*(10**9) #coloumb-const
	c = 299792458 #speed-of-light-vacuum
	bol = 1.38065*(10**-23) #boltsmann-const
	gra = 9.80665 #accelaration-of-gravity
	ryd = 1.0973731568539*(10**7) #rydberg-const
	pnk = 6.62607004*(10**-34) #planck-const
	sph = 4.18 #specific-heat-water
	rpc = 1.05457182*(10**-34) #reduced-planck-const
	muo = 4*(3.141592653589)*(10**-7) #vacuum-magnetic-perm
	atm = 101325 #standard-atmos-in-pascal
	fcc = 0.553960278365 #chandrasekar-first-const
	scc = 0.541926070139 #chandrasekar-second-const
